\copy (SELECT distinct measurement_concept_id as "measurement_concept_id:ID(Measurement)", mc.concept_name as measurement_concept_name FROM public.measurement c left join public.concept mc on mc.concept_id = c.measurement_concept_id left join public.concept uc on uc.concept_id = c.unit_concept_id where unit_concept_id is not null and uc.concept_name is not null order by measurement_concept_id asc) to 'lab.csv' WITH CSV HEADER;