\copy (select distinct drug_exposure_id as "drug_exposure_id:ID(DrugExposure)", dc.concept_name as drug_concept_name, drug_concept_id, drug_exposure_start_date, drug_exposure_end_date, drug_type_concept_id, dtc.concept_name as drug_type_concept_name, route_concept_id, rc.concept_name as route_concept_name, stop_reason from public.drug_exposure as c left join public.concept dc on dc.concept_id = c.drug_concept_id left join public.concept dtc on dtc.concept_id = c.drug_type_concept_id left join public.concept rc on rc.concept_id = c.route_concept_id) to 'drug.csv' WITH CSV HEADER;